package corejava;

public class numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int i;
      int j;
       for(i=0;i<=5;i++)
       {
    	 for(j=0;j<=i;j++)
    	 {
    		 System.out.print(j+"");
    	 }
    	 System.out.print("\n");
       }
	}

}
