package corejava;

public class patternpro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int i;
       int j;
       for(i=1;i<=5;i++)
       {
    	   for(j=1;j<=i;j++)
    	   {
    		   System.out.print("*");
    	   }
    	   System.out.print("\n");
       }
	}

}
